import React, { useEffect, useState } from 'react';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import './Header.css'; // Import custom CSS for Header styling

const Header = () => {
  const [scroll, setScroll] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      if (currentScrollY > 0) {
        setScroll(true);
      } else {
        setScroll(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <AppBar
      position="fixed"
      className={scroll ? 'app-bar scrolled' : 'app-bar'}
      elevation={0}
      style={{ backgroundColor: scroll ? 'white' : 'transparent', color: 'black' }}
    >
      <Toolbar>
        <Typography variant="h6" style={{ flexGrow: 1 }}>
          My Portfolio
        </Typography>
        <Button component={Link} to="home" color="inherit">
          Home
        </Button>
        <Button component={Link} to="aboutMe" color="inherit">
          About
        </Button>
        <Button component={Link} to="projects" color="inherit">
          Projects
        </Button>
        <Button component={Link} to="contact" color="inherit">
          Contact
        </Button>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
